        //     console.log('res', res)
        // })
        // ctx.body = {
        //     code: 200,
        //     data: newCaptcha.data
        // }
        ctx.body = {
            code: 200,
            data: {
                note1: '123',
                note2: 22,
                note3: 'asdasd'
            }
        }
    }


    async list(ctx) {
        // 获取query数据
        const query = ctx.request.query
        const { current, pageSize } = query
        console.log(current, pageSize)
        let tableListDataSource = genList(1, 100)
        const result = {
            data: tableListDataSource,
            total: tableListDataSource.length,
            pageSize,
            current
        };
        ctx.body = {
