import config from '../config/index'
import {
    requireCheck
} from "../utils/requireCheck";

const {
    messageTypeTable
} = config.TABLENAMELIST

class messageTypeController {
    constructor() { }

    // 系列列表
    async getMessageTypeList(ctx) {
        const {
            current,
