        const {
            id,
            typeName
        } = ctx.request.body
        const params = {
            id,
            typeName
        }
        const result = await sendEdit(params, messageTypeTable)
        ctx.body = {
            code: result.affectedRows === 1 ? 200 : 500
        }
    }
}

export default new messageTypeController()

