        setValue(query.sid, newCaptcha.text, 3 * 600)
        getValue(query.sid).then(res => {
            console.log('res', res)
        })
        ctx.body = {
            code: 200,
            data: newCaptcha.data
        }
    }
    async list(ctx) {
        // 获取query数据
        const query = ctx.request.query
        console.log('query', query)
    }
}

export default new PublicController()

